// Get inputs from both API calls
const items = $input.all();

// Initialize variables
let flightData = [];
let hotelData = [];
let bookingData = {};

// Process flight data (first item from merge)
try {
  const flightItem = items[0]?.json;
  if (flightItem) {
    // If 'data' field exists and is a string, parse it
    if (flightItem.data && typeof flightItem.data === 'string') {
      const parsed = JSON.parse(flightItem.data);
      flightData = parsed.data || [];
    } 
    // If already an object with data array
    else if (flightItem.data && Array.isArray(flightItem.data)) {
      flightData = flightItem.data;
    }
    // Check for booking_data
    if (flightItem.booking_data) {
      bookingData = flightItem.booking_data;
    }
  }
} catch (e) {
  console.log("Error processing flight data:", e);
}

// Process hotel data (second item from merge)
try {
  const hotelItem = items[1]?.json;
  if (hotelItem) {
    // If 'data' field exists and is a string, parse it
    if (hotelItem.data && typeof hotelItem.data === 'string') {
      const parsed = JSON.parse(hotelItem.data);
      hotelData = parsed.data || [];
    }
    // If already an object with data array
    else if (hotelItem.data && Array.isArray(hotelItem.data)) {
      hotelData = hotelItem.data;
    }
  }
} catch (e) {
  console.log("Error processing hotel data:", e);
}

// Set default booking data if not found
if (!bookingData || Object.keys(bookingData).length === 0) {
  bookingData = {
    origin: "JFK",
    destination: "LHR",
    departure_date: "2025-11-15",
    return_date: "2025-11-22",
    travelers_count: 2,
    traveler_name: "Guest User",
    traveler_email: "test@example.com",
    request_id: Date.now().toString()
  };
}

// Process flights - now flightData is properly an array
let processedFlights = [];
if (Array.isArray(flightData) && flightData.length > 0) {
  processedFlights = flightData.slice(0, 3).map(offer => {
    try {
      const outbound = offer.itineraries[0];
      const returnFlight = offer.itineraries[1];

      return {
        outbound: {
          flight_number: outbound.segments[0].carrierCode + outbound.segments[0].number,
          airline: outbound.segments[0].carrierCode,
          departure_time: outbound.segments[0].departure.at,
          arrival_time: outbound.segments[outbound.segments.length - 1].arrival.at,
          duration: outbound.duration,
          stops: outbound.segments.length - 1
        },
        return: returnFlight ? {
          flight_number: returnFlight.segments[0].carrierCode + returnFlight.segments[0].number,
          airline: returnFlight.segments[0].carrierCode,
          departure_time: returnFlight.segments[0].departure.at,
          arrival_time: returnFlight.segments[returnFlight.segments.length - 1].arrival.at,
          duration: returnFlight.duration,
          stops: returnFlight.segments.length - 1
        } : null,
        price: parseFloat(offer.price.total),
        currency: offer.price.currency || "USD"
      };
    } catch (e) {
      return null;
    }
  }).filter(f => f !== null);
}

// Default flights if none processed
if (processedFlights.length === 0) {
  processedFlights = [{
    outbound: {
      flight_number: "AA123",
      airline: "AA",
      departure_time: bookingData.departure_date + "T10:00:00",
      arrival_time: bookingData.departure_date + "T18:00:00",
      duration: "PT8H",
      stops: 0
    },
    return: {
      flight_number: "AA124",
      airline: "AA",
      departure_time: bookingData.return_date + "T14:00:00",
      arrival_time: bookingData.return_date + "T22:00:00",
      duration: "PT8H",
      stops: 0
    },
    price: 500,
    currency: "USD"
  }];
}

// Process hotels - now hotelData is properly an array
let processedHotels = [];
if (Array.isArray(hotelData) && hotelData.length > 0) {
  processedHotels = hotelData.slice(0, 3).map(hotel => {
    try {
      return {
        hotel_id: hotel.hotelId,
        name: hotel.name,
        distance: hotel.distance ? `${hotel.distance.value} ${hotel.distance.unit}` : "Unknown",
        address: hotel.address ? 
          `${hotel.address.lines ? hotel.address.lines.join(", ") : ""}, ${hotel.address.cityName || bookingData.destination}` : 
          bookingData.destination,
        price_per_night: 100 + Math.floor(Math.random() * 150),
        rating: 4.0 + (Math.random() * 0.5)
      };
    } catch (e) {
      return null;
    }
  }).filter(h => h !== null);
}

// Default hotel if none processed
if (processedHotels.length === 0) {
  processedHotels = [{
    hotel_id: "DEFAULT001",
    name: "Grand Hotel",
    distance: "2.5 KM",
    address: bookingData.destination,
    price_per_night: 150,
    rating: 4.3
  }];
}

// Return the final processed data
return {
  booking_data: bookingData,
  flight_options: processedFlights,
  hotel_options: processedHotels,
  selected_flight: processedFlights[0],
  selected_hotel: processedHotels[0]
};
